import { useEffect, useState } from "react";

const AddPassword = ({ contract, account, getPasswords }) => {
  const [newPassword, setNewPassword] = useState("");
  const [loading, setLoading] = useState(false);

  async function createPassword(password) {
    if (!contract || !account) {
      console.error(
        "Web3 or contract not initialized or account not connected."
      );
      return;
    }
    try {
      setLoading(true);
      await contract.methods.createPassword(password).send({ from: account });
      getPasswords();
    } catch (error) {
      console.error("User rejected request:", error);
    } finally {
      setLoading(false);
    }
  }

  return (
    <form
      id="tweetForm"
      onSubmit={(e) => {
        e.preventDefault();
        createPassword(newPassword);
      }}
    >
      <textarea
        id="tweetContent"
        rows="4"
        placeholder="What's happening?"
        value={newPassword}
        onChange={(e) => setNewPassword(e.target.value)}
      />
      <br />
      <button id="tweetSubmitBtn" style={{ marginLeft:'260px', background:'#eb57ad'}} disabled={loading} type="submit">
  {loading ? <div className="spinner"></div> : <>Enter</>}
</button>

    </form>
  );
};

export default AddPassword;
